import React, { useState, useEffect} from 'react'
import { motion } from 'framer-motion';

import { images } from '../../constants';
import './About.scss';

const abouts = [
  {title: 'Web Development',description:"describing", ImgURL: images.about01},
  {title: 'Web Design',description:"describing", ImgURL: ""},
  {title: 'Business Mangement',description:"describing", ImgURL: ""},
  {title: 'Mathematic Modeling',description:"describing", ImgURL: ""}
];

const About = () => {
  return (
    <>
      <h2 className='head-text'>I know that 
      <span>good design</span>
      <br/>
      means
      <span>good business.</span>
      </h2>

      <div className='app__profiles'>

        {abouts.map((about, index) => (

          <motion.div

            whileInView={{opacity:1}}
            whileHover={{scale:1.1}}
            transition={{duration:0.5,type:'tween'}}
            className="app__profile-item"
            key={about.title + index}
          >
            <img src={about.ImgURL} alt={about.title} />
            <h2 className='bold-text' style={{marginTop:20}}>{about.title}</h2>
            <p className='p-text' style={{marginTop:20}}>{about.title}</p>
          </motion.div>

        ))}

      </div>
    </>
  )
}

export default About
